/*
 * activeNavbar - a jquery plugin which assign a class="active" to a navbar 
 * if the URL match with the href from that navbar
 * 
 * @author Vladu Sorin
 * 
 * @version 1.0
 *
 */

var activeNavbar={init:function(e){if(typeof e=="undefined"){e="active"}var t=activeNavbar.trim(document.URL);$(".activeNavbar li").each(function(n,r){var i=$(r);var s=i.find("a");var o=activeNavbar.trim(s.attr("href"));if(typeof s.attr("href")!="undefined"&&o==t){i.addClass(e)}})},trim:function(e){var t=e.indexOf("#");if(t>0){e=e.substring(0,t)}if(e[e.length-1]=="/"){e=e.substring(0,e.length-1)}return e}}
